mapper.select:
SELECT D_CD, D_NM, LISTAGG(M_NM, ',') within group (order by M_NM) M_NM FROM( SELECT D_CD, D_NM, M_CD, M_NM FROM VW_DISEASE_MEDI_DEPT WHERE D_CD IN (SELECT D_CD FROM ( SELECT d_cd, LISTAGG(s_cd, ',') within group (order by s_cd) s_cd FROM symptom_disease group by d_cd)
AND s_cd LIKE '%'|| #{s_cd} || '%'
)) group by D_CD, D_NM


SELECT distinct regexp_substr(SRCH_TAG, '[^.]+', 1, LEVEL) AS SRCH_TAG
FROM USER_SRCH_TAG WHERE u_id = #{u_id}
CONNECT BY LEVEL <= length(regexp_replace(SRCH_TAG, '[^.]+',''))+1
